(function ($) {
    $.bottominview = function (element, settings) {
        var fold = $(window).scrollTop() + $(window).height();
        return fold > $(element).offset().top + $(element).height()-100
    };
    $.abovethetop = function (element, settings) {
        var top = $(window).scrollTop();
        return top >= $(element).offset().top - $(element).height() + $(element).height() - 100
    };
    $.extend($.expr[":"], {
        "above-the-top": function (a, i, m) {
            return $.abovethetop(a, {
                threshold: 0
            })
        },
        "bottom-in-view": function (a, i, m) {
            return $.bottominview(a, {
                threshold: 0
            })
        }
    })
})(jQuery);